import React, { useEffect, useState } from 'react';
import axios from 'axios';
const API = "http://localhost:5000";

export default function Gallery(){
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState('all');
  const [servicePrices, setServicePrices] = useState([]);

  useEffect(()=>{
    axios.get(`${API}/api/media`).then(r=>setItems(r.data)).catch(()=>{});
    axios.get(`${API}/api/prices`).then(r=>setServicePrices(r.data)).catch(()=>{});
  },[]);

  const displayed = items.filter(i => filter==='all'? true : i.category===filter);

  return (
    <div>
      <section className="hero">
        <div className="hero-text">
          <h1>Keshvam Graphic</h1>
          <p>Creative Prints, Unique Expressions</p>
        </div>
        <div className="hero-image" />
      </section>

      <div className="controls">
        <label>Filter:</label>
        <select value={filter} onChange={e=>setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="patrika">Patrika</option>
          <option value="invitation">Invitation</option>
          <option value="plate">Number Plate</option>
          <option value="video">Video</option>
        </select>
      </div>

      <div className="grid">
        {displayed.length===0 && <div className="empty">No items yet — Admin can upload from Admin panel.</div>}
        {displayed.map(it=>(
          <div className="card" key={it._id || it.id}>
            {it.mimeType && it.mimeType.startsWith('video') ? (
              <video controls className="media" src={it.url} />
            ) : (
              <img className="media" src={it.url} alt={it.originalName} />
            )}

            <div className="card-body" style={{padding:"8px"}}>
              <div className="title" style={{fontWeight:"700", fontSize:"16px", color:"var(--dark)"}}>
                {it.title || it.originalName}
              </div>

              {(() => {
                // determine price to show: prefer item.price, then item.priceRange, then service fallback
                const formatPrice = (s) => {
                  if(!s) return null;
                  const str = String(s).trim();
                  if(!str) return null;
                  return /₹/.test(str) ? str : `₹ ${str}`;
                };

                let priceStr = formatPrice(it.price) || formatPrice(it.priceRange);
                if(!priceStr && servicePrices && servicePrices.length){
                  const cat = (it.category||'').toLowerCase();
                  const findBy = (keywords)=> servicePrices.find(p=> keywords.some(k=> (p.name||'').toLowerCase().includes(k)));
                  let svc = null;
                  if(cat.includes('video')) svc = findBy(['video','reel']);
                  else if(cat.includes('patrika') || cat.includes('invitation')) svc = findBy(['invitation','wedding','patrika']);
                  else if(cat.includes('plate') || cat.includes('number')) svc = findBy(['plate','number plate']);
                  else if(cat.includes('frame')) svc = findBy(['frame','photo frame']);
                  if(svc) priceStr = formatPrice(svc.price || svc.range);
                }

                return priceStr ? (
                  <div className="price" style={{
                    margin:"6px 0",
                    padding:"6px 10px",
                    background:"var(--blue)",
                    color:"white",
                    display:"inline-block",
                    borderRadius:"6px",
                    fontWeight:"600"
                  }}>
                    {priceStr}
                  </div>
                ) : null;
              })()}

              <div className="cat" style={{color:"var(--muted)", fontSize:"12px"}}>
                {it.category}
              </div>

              <div className="date" style={{marginTop:"4px", fontSize:"12px", color:"#7487a3"}}>
                {new Date(it.uploadedAt).toLocaleString()}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
