import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
const API = "http://localhost:5000";

export default function AdminPanel(){
  const [file, setFile] = useState(null);
  const [category, setCategory] = useState('patrika');
  const [items, setItems] = useState([]);
  const nav = useNavigate();

  useEffect(()=>{ const token = localStorage.getItem('kg_token'); if(!token){ nav('/admin'); return; } fetchItems(); },[]);

  const fetchItems = async ()=>{ const res = await axios.get(`${API}/api/media`); setItems(res.data); };
  const upload = async ()=>{
    if(!file) return alert('Choose file');
    const form = new FormData(); form.append('file', file); form.append('category', category);
    const token = localStorage.getItem('kg_token');
    await axios.post(`${API}/api/admin/upload`, form, { headers: { Authorization: `Bearer ${token}` } });
    fetchItems();
  };
  const remove = async (id)=>{
    if(!confirm('Delete?')) return;
    const token = localStorage.getItem('kg_token');
    await axios.delete(`${API}/api/admin/${id}`, { headers: { Authorization: `Bearer ${token}` } });
    fetchItems();
  };

  return (
    <div className="admin-wrap">
      <div className="center-card">
        <h2>Admin Panel - Upload</h2>
        <select value={category} onChange={e=>setCategory(e.target.value)} className="input">
          <option value="patrika">Patrika</option>
          <option value="invitation">Invitation</option>
          <option value="plate">Number Plate</option>
          <option value="video">Video</option>
        </select>
        <input type="file" onChange={e=>setFile(e.target.files[0])} className="input" />
        <button className="btn" onClick={upload}>Upload</button>
      </div>

      <div className="admin-list">
        <h3>Uploaded Items</h3>
        <div className="grid">
          {items.map(it=>(
            <div className="card" key={it.id}>
              {it.mimeType && it.mimeType.startsWith('video') ? (
                <video controls className="media" src={`${API}${it.url}`} />
              ) : (
                <img className="media" src={`${API}${it.url}`} alt={it.originalName} />
              )}
              <div className="card-body">
                <div className="name">{it.originalName}</div>
                <div className="cat">{it.category}</div>
                <button className="btn small" onClick={()=>remove(it.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
