import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "./adminStyle.css"
const API = process.env.REACT_APP_API || `${window.location.protocol}//${window.location.hostname}:5000`;

export default function AdminLogin(){
  const [pass, setPass] = useState('');
  const nav = useNavigate();
  const doLogin = async ()=>{
    try{
      const res = await axios.post(`${API}/api/admin/login`, { password: pass });
      if(res.data.token){ localStorage.setItem('kg_token', res.data.token); nav('/admin/panel'); }
    }catch{ alert('Login failed'); }
  };
  return (
  <div className="main-container2">
<div className="center-card">
      <h2>Admin Login</h2>
      <input className="input" type="password" value={pass} onChange={e=>setPass(e.target.value)} placeholder="Admin password"/>
      <button className="btn" onClick={doLogin}>Login</button>
      {/* <p className="hint">Default admin password: 1234 (change .env)</p> */}
    </div></div>
  );
}
