import React, { useEffect, useState } from 'react';
import axios from 'axios';
const API = "http://localhost:5000";

export default function Gallery(){
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState('all');
  const [servicePrices, setServicePrices] = useState([]);

  useEffect(()=>{
    axios.get(`${API}/api/media`).then(r=>setItems(r.data)).catch(()=>{});
    axios.get(`${API}/api/prices`).then(r=>setServicePrices(r.data)).catch(()=>{});
  },[]);

  const displayed = items.filter(i => filter==='all'? true : i.category===filter);

  const findServicePrice = (category) => {
    if(!category || !servicePrices || servicePrices.length===0) return null;
    const c = (category||'').toLowerCase();
    // try to match known keywords
    const lookup = (keywords) => servicePrices.find(p => keywords.some(k => (p.name||'').toLowerCase().includes(k)));

    if(c.includes('video')) return lookup(['video','reel','reels']);
    if(c.includes('patrika') || c.includes('invitation')) return lookup(['invitation','wedding','patrika']);
    if(c.includes('plate') || c.includes('number')) return lookup(['plate','number plate']);
    if(c.includes('frame')) return lookup(['frame','photo frame']);
    return null;
  };

  const formatPriceString = (s) => {
    if(!s) return null;
    const str = String(s).trim();
    if(str === '') return null;
    if(str.includes('₹')) return str;
    // if it contains any digit, assume numeric and prepend ₹
    if(/[0-9]/.test(str)) return '₹ ' + str;
    return str;
  };

  const getPriceForItem = (it) => {
    if(!it) return null;
    // prefer explicit price on item
    if(it.price && String(it.price).trim() !== '') return formatPriceString(it.price);
    if(it.priceRange && String(it.priceRange).trim() !== '') return formatPriceString(it.priceRange);
    // fallback to service price
    const svc = findServicePrice(it.category);
    if(svc) return formatPriceString(svc.price || svc.range);
    return null;
  };

  return (
    <div>
      <section className="hero">
        <div className="hero-text">
          <h1>Keshvam Graphic</h1>
          <p>Creative Prints, Unique Expressions</p>
        </div>
        <div className="hero-image" />
      </section>

      <div className="controls">
        <label>Filter:</label>
        <select value={filter} onChange={e=>setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="patrika">Patrika</option>
          <option value="invitation">Invitation</option>
          <option value="plate">Number Plate</option>
          <option value="video">Video</option>
        </select>
      </div>

      <div className="grid">
        {displayed.length===0 && <div className="empty">No items yet — Admin can upload from Admin panel.</div>}
        {displayed.map(it=>(
          <div className="card" key={it.id}>
            {it.mimeType && it.mimeType.startsWith('video') ? (
              <video controls className="media" src={it.url} />
            ) : (
              <img className="media" src={it.url} alt={it.originalName} />
            )}

            <div className="card-body" style={{padding:"8px"}}>
              <div className="title" style={{fontWeight:"700", fontSize:"16px", color:"var(--dark)"}}>
                {it.title || it.originalName}
              </div>

              {getPriceForItem(it) ? (
                <div className="price" style={{
                  margin:"6px 0",
                  padding:"6px 10px",
                  background:"var(--blue)",
                  color:"white",
                  display:"inline-block",
                  borderRadius:"6px",
                  fontWeight:"600"
                }}>
                  {getPriceForItem(it)}
                </div>
              ) : null}

              <div className="cat" style={{color:"var(--muted)", fontSize:"12px"}}>
                {it.category}
              </div>

              <div className="date" style={{marginTop:"4px", fontSize:"12px", color:"#7487a3"}}>
                {new Date(it.uploadedAt).toLocaleString()}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
