import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Gallery from './Gallery';
import AdminLogin from './AdminLogin';
import AdminPanel from './AdminPanel';
import PriceList from "./PriceList";
import AdminPriceManager from "./AdminPriceManager";

import './styles.css';

export default function App() {
  return (
    <BrowserRouter>
      <header className="site-header">
        <div className="brand">
          <div className="logo-dot"></div>
          <div>
            <div className="shop-name">Keshvam Graphic</div>
            <div className="tagline">Creative Prints, Unique Expressions</div>
          </div>
        </div>
        <nav className="nav-links">
          <Link to="/" className="nav-item">Home</Link>
          <Link to="/admin" className="nav-item">Admin</Link>
        </nav>
      </header>

      <main className="main-container">
        <Routes>

          {/* Home: Show Gallery + PriceList only */}
          <Route path="/" element={
            <div className="home-layout">
              <div className="gallery-panel">
                <Gallery />
              </div>
              <aside className="price-panel">
                <PriceList />
              </aside>
            </div>
          } />

          {/* Admin Login */}
          <Route path="/admin" element={<AdminLogin />} />

          {/* Admin Panel (only accessible after login) */}
          <Route path="/admin/panel" element={<AdminPanel />} />

          {/* Admin Price Manager Page (only admin should see this) */}
          <Route path="/admin/manage-prices" element={
            localStorage.getItem("kg_token")
              ? <AdminPriceManager />
              : <AdminLogin />
          } />

        </Routes>
      </main>

      <footer className="site-footer">
        © {new Date().getFullYear()} Keshvam Graphic — Patrika, Invitation Cards, Number Plates & Videos
      </footer>
    </BrowserRouter>
  );
}
