import React, { useEffect, useState } from 'react';
import axios from 'axios';
const API = 'http://localhost:5000';

export default function PhotoFrame(){
  const [frames, setFrames] = useState([]);

  useEffect(()=>{
    axios.get(`${API}/api/media`).then(r=>{
      const all = r.data || [];
      const onlyFrames = all.filter(i => (i.category||'').toLowerCase() === 'frame');
      setFrames(onlyFrames);
    }).catch(()=>{});
  },[]);

  return (
    <section className="photo-frame">
      <div className="photo-frame-inner">
        <div className="photo-frame-text">
          <h3>Photo Frames</h3>
          <p>Handcrafted and ready-to-hang frames uploaded by the admin. Click any sample to view larger.</p>
        </div>

        <div className="frame-grid">
          {frames.length === 0 && (
            <div className="empty" style={{padding:'20px'}}>No frames yet â€” admin can upload frames from the admin panel.</div>
          )}

          {frames.map(f => (
            <div className="frame-sample" key={f._id}>
              {f.mimeType && f.mimeType.startsWith('video') ? (
                <video className="frame-img" src={f.url} controls />
              ) : (
                <img className="frame-img" src={f.url} alt={f.title || f.originalName} />
              )}
              <div className="frame-label">{f.title || f.originalName}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
