import axios from 'axios';

const API = process.env.REACT_APP_API || `${window.location.protocol}//${window.location.hostname}:5000`;

// Expose globally for quick debugging in browser console
window.__API_BASE__ = API;
console.info('[apiBase] resolved API base ->', API);

// Set axios default base URL so relative axios calls work
axios.defaults.baseURL = API;

export default API;
