import React, { useEffect, useState } from "react";
import axios from "axios";
import "./PriceList.css";

const API = process.env.REACT_APP_API || `${window.location.protocol}//${window.location.hostname}:5000`;

export default function PriceList(){
  const [prices, setPrices] = useState([]);

  useEffect(()=>{
    axios.get(`${API}/api/prices`).then(r=>setPrices(r.data)).catch(()=>setPrices([]));
  },[]);

  return (
    <section className="price-list">
      <div className="price-head">
        <h2 className="price-title">Price List</h2>
        <p className="price-sub">Clear pricing for all media & finishes</p>
      </div>

      <div className="price-table">
        {prices.length === 0 && (
          <div className="price-empty">No prices available</div>
        )}

        {prices.map((p,i)=>(
          <article className="price-card" key={p._id || i}>
            <div className="price-info">
              <div className="price-name">{p.name}</div>
              {p.desc && <div className="price-desc">{p.desc}</div>}
            </div>
            <div className="price-range">₹ {p.price ?? p.priceRange ?? "—"}</div>
          </article>
        ))}
      </div>
    </section>
  );
}
