import React, { useEffect, useState } from 'react';
import axios from 'axios';
const API = "http://localhost:5000";

export default function Gallery(){
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(()=>{ axios.get(`${API}/api/media`).then(r=>setItems(r.data)); },[]);

  const displayed = items.filter(i => filter==='all'? true : i.category===filter);

  return (
    <div>
      <section className="hero">
        <div className="hero-text">
          <h1>Keshvam Graphic</h1>
          <p>Creative Prints, Unique Expressions</p>
        </div>
        <div className="hero-image" />
      </section>

      <div className="controls">
        <label>Filter:</label>
        <select value={filter} onChange={e=>setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="patrika">Patrika</option>
          <option value="invitation">Invitation</option>
          <option value="plate">Number Plate</option>
          <option value="video">Video</option>
        </select>
      </div>

      <div className="grid">
        {displayed.length===0 && <div className="empty">No items yet â€” Admin can upload from Admin panel.</div>}
        {displayed.map(it=>(
          <div className="card" key={it.id}>
            {it.mimeType && it.mimeType.startsWith('video') ? (
              <video controls className="media" src={`${API}${it.url}`} />
            ) : (
              <img className="media" src={`${API}${it.url}`} alt={it.originalName} />
            )}
            {/* <div className="card-body">
              <div className="cat">{it.category}</div>
              <div className="name">{it.originalName}</div>
              <div className="date">{new Date(it.uploadedAt).toLocaleString()}</div>
            </div> */}


          </div>
        ))}
      </div>
    </div>
  );
}
