import React, { useEffect, useState } from "react";
import axios from "axios";
import "./priceListStyle.css";

const API = process.env.REACT_APP_API || `${window.location.protocol}//${window.location.hostname}:5000`;

export default function PriceList(){
  const [prices, setPrices] = useState([]);

  useEffect(()=>{
    axios.get(`${API}/api/prices`).then(r=>setPrices(r.data)).catch(()=>setPrices([]));
  },[]);

  return (
    <section className="pl-root">
      <header className="pl-header">
        <h2 className="pl-title">Price List</h2>
        <p className="pl-sub">Transparent pricing for all media & finishes</p>
      </header>

      <div className="pl-grid">
        {prices.length === 0 && (
          <div className="pl-empty">No pricing available</div>
        )}

        {prices.map((p, i) => (
          <div className="pl-card" key={p._id || i}>
            <div className="pl-left">
              <div className="pl-name">{p.name}</div>
              {p.desc && <div className="pl-desc">{p.desc}</div>}
            </div>
            <div className="pl-price">₹ {p.price ?? p.priceRange ?? "—"}</div>
          </div>
        ))}
      </div>
    </section>
  );
}
