import axios from 'axios';

// Resolve API base. If REACT_APP_API is provided, use it.
// Otherwise default to http://<host>:5000 — force http for local backend
const envApi = process.env.REACT_APP_API;
let API;
if (envApi) {
	API = envApi;
} else {
	// Force http to avoid cases where the frontend is served over https
	// and the browser then tries https://<host>:5000 which the node server doesn't serve.
	API = `http://${window.location.hostname}:5000`;
}

// Expose globally for quick debugging in browser console
window.__API_BASE__ = API;
console.info('[apiBase] resolved API base ->', API);

// Set axios default base URL so relative axios calls work
axios.defaults.baseURL = API;

export default API;
